import './App.css';
import products from './data.js'
import Card from './components/Card.jsx'
import Category from './components/Category.jsx'
import categories from './categories.js'
import { useEffect, useState } from 'react';
function App() {

  const [myproducts, setProducts] = useState(products)

  function getCategory(categoryName) {
    let newProducts = products.filter(product => product.category === categoryName)
    setProducts(newProducts)
  }

  function sortProducts() {
    const sortedProducts = [...myproducts];
    sortedProducts.sort((a, b) => a.price - b.price);
    setProducts(sortedProducts)
  }

  function sortProductsByHigh() {
    const sortedProducts = [...myproducts];
    sortedProducts.sort((a, b) => b.price - a.price);
    setProducts(sortedProducts)
  }


  const [currentPage, setCurrentPage] = useState(1)

  const paginate = (pageNumber) => {

    const indexOfLastItem = currentPage * 6;
    const indexOfFirstItem = indexOfLastItem - 6;
    console.log(indexOfFirstItem,indexOfLastItem)
    const currentItems = myproducts.slice(indexOfFirstItem, indexOfLastItem);
    console.log(currentItems)
    if(pageNumber === 0){
      setCurrentPage(1)
    }
    else if(pageNumber === Math.ceil(products.length/6)){
      setCurrentPage(Math.ceil(products.length/6))
    }
    else{
      setCurrentPage(pageNumber);
    }
    setProducts(currentItems)
  };

  useEffect(() => {
    paginate()
  },[])


  return (
    <div className='App_container'>

      <div className="navbar">
        <div className="logo">
          <span>DevTown Assignment</span>
        </div>
      </div>
      <Category categories={categories} getCategory={getCategory}></Category>
      <div className='App_button'>

        <button className='creative-button_' onClick={sortProducts}>Sort Low to High</button>
        <button className='creative-button_' onClick={sortProductsByHigh}>Sort High to Low</button>

      </div>

      <div className="App">
        {
          myproducts.map(productData => <Card image={productData.image} title={productData.title} price={productData.price} description={productData.description} />)
        }
      </div>

      <div id="pagination">
        <button id="prevPage" className='page' onClick={() => {paginate(currentPage-1)}}>&larr; Previous</button>
        <button id="nextPage" className='page' onClick={() => {paginate(currentPage+1)}}>Next &rarr;</button>
      </div>
    </div>
  );
}

export default App;
