import './App.css';
import products from './data.js'
import Card from './components/Card.jsx'
import Category from './components/Category.jsx'
import categories from './categories.js'
import { useState } from 'react';
function App() {

  const [myproducts,setProducts] = useState(products)

  function getCategory(categoryName){
    let newProducts = products.filter(product => product.category === categoryName)
    setProducts(newProducts)
  }

  function sortProducts(){
    const sortedProducts = myproducts.sort((a, b) => b.price - a.price);
    console.log(sortedProducts)
    // setProducts(sortedProducts)
  }
  return (
    <div className='App_container'>
      <Category categories = {categories} getCategory={getCategory}></Category>

      <button onClick={sortProducts}>Sort Low to high</button>
    <div className="App">
      {
        myproducts.map(productData => <Card image = {productData.image} title = {productData.title} price = {productData.price} description = {productData.description} />)
      }
      </div>
    </div>
  );
}

export default App;
